// Pak de data uit de eerste node
const items = $input.all();
const data = items[0].json;

// Extract basis info
const opdrachtnummer = data.opdrachtnummer || 
                      data.filename?.replace(/^Offerte\s*/, '').replace(/\.pdf$/, '').trim() ||
                      null;

const customerName = data.trimmedPdfText?.match(/Naam\s+(.+)/)?.[1]?.trim() || null;

// Process alleen deur elementen
const deurSpecificaties = [];

(data.merkElements || []).forEach(element => {
  const body = element.body || '';

  // Extract element type
  const elementType = body.match(/Soort kozijn\s+(.+?)(?:\n|$)/)?.[1]?.trim() || '';

  // Check of het een deur is
  const isDoor = elementType.toLowerCase().includes('deur') || 
                 elementType.toLowerCase().includes('pui');

  if (!isDoor) return; // Skip niet-deur elementen

  // Extract dimensies
  const dimensions = body.match(/(\d{3,4})x(\d{3,4})mm/);
  const width = dimensions ? parseInt(dimensions[1]) : null;
  const height = dimensions ? parseInt(dimensions[2]) : null;

  // Extract locatie
  const location = body.match(/Plaats\s+(.+?)(?:\n|$)/)?.[1]?.trim() || '';

  // Extract kleuren (als ze in de body staan)
  const kleurKozijnBinnen = body.match(/Kozijn binnen\s+(.+?)(?:\n|$)/)?.[1]?.trim();
  const kleurKozijnBuiten = body.match(/Kozijn buiten\s+(.+?)(?:\n|$)/)?.[1]?.trim();
  const kleurVleugelBinnen = body.match(/Vleugel binnen\s+(.+?)(?:\n|$)/)?.[1]?.trim();
  const kleurVleugelBuiten = body.match(/Vleugel buiten\s+(.+?)(?:\n|$)/)?.[1]?.trim();

  // Extract deur specifieke details
  const modelDeur = body.match(/Model deur\s+(.+?)(?:\n|$)/)?.[1]?.trim();
  const glasType = body.match(/Glas deurvleugel\s+(.+?)(?:\n|$)/)?.[1]?.trim();
  const cilinder = body.match(/Type cilinder\s+(.+?)(?:\n|$)/)?.[1]?.trim();
  const dorpel = body.match(/Soort dorpel\s+(.+?)(?:\n|$)/)?.[1]?.trim();

  // Extract prijs
  const totalPrice = body.match(/Totaal\s+€\s*([\d.,]+)/)?.[1];
  const priceValue = totalPrice ? parseFloat(totalPrice.replace('.', '').replace(',', '.')) : null;

  // Build deur specificatie object
  const deurSpec = {
    // PRIMARY & BASIC INFO
    "Element ID": `${opdrachtnummer} - ${element.header.replace(/^Merk\s*/i, '')}`,
    "Element ID Ref": element.header.replace(/^Merk\s*/i, '').trim(),
    "Opdrachtnummer": opdrachtnummer,
    "Klantnaam": customerName,
    "Element Type": elementType,
    "Locatie": location,

    // AFMETINGEN
    "Geoffreerde Afmetingen": width && height ? `${width}mm x ${height}mm` : null,
    "Breedte (mm)": width,
    "Hoogte (mm)": height,

    // DEUR SPECIFICATIES
    "Model Deur": modelDeur || null,
    "Type Profiel": "Verdiept",
    "Type Kozijn": "Stomp",
    "Draairichting": "Wordt met inmeten bepaald",
    "Glas Deurvleugel": glasType || null,
    "Type Cilinder": cilinder || null,
    "Soort Onderdorpel": dorpel || "Hardstenen onderdorpel",
    "Afwatering": "Onderkant",
    "Binnenafwerking": "Kap 6",

    // KLEUREN
    "Kleur Kozijn Binnen": kleurKozijnBinnen || null,
    "Kleur Kozijn Buiten": kleurKozijnBuiten || null,
    "Kleur Deurvleugel Binnen": kleurVleugelBinnen || null,
    "Kleur Deurvleugel Buiten": kleurVleugelBuiten || null,

    // SALES WORKFLOW
    "Definitieve Element Prijs": priceValue,
    "Verkoop Review Status": "Concept",
    "Opmerkingen voor Binnendienst": null
  };

  // Clean null values voor Airtable
  const cleanedDeurSpec = {};
  Object.keys(deurSpec).forEach(key => {
    if (deurSpec[key] !== null && deurSpec[key] !== undefined) {
      cleanedDeurSpec[key] = deurSpec[key];
    }
  });

  deurSpecificaties.push(cleanedDeurSpec);
});

// Return elk deur element als apart item voor Airtable batch processing
return deurSpecificaties.map(spec => ({ json: spec }));

// Pak de data uit de eerste node
const items = $input.all();
const data = items[0].json;

// Helper functies
const parsePrice = (str) => {
  if (!str) return null;
  const cleaned = str.toString()
    .replace(/€/g, '')
    .replace(/\./g, '')
    .replace(',', '.')
    .replace(/\s/g, '')
    .trim();
  const parsed = parseFloat(cleaned);
  return isNaN(parsed) ? null : parsed;
};

// Extract opdrachtnummer
const opdrachtnummer = (() => {
  if (data.filename) {
    const match = data.filename.match(/(\d{7}[A-Z]{2})/);
    if (match) return match[1];

    const cleaned = data.filename
      .replace(/^offerte\s*/i, '')
      .replace(/\.pdf$/i, '')
      .trim()
      .split(' ')[0];

    if (cleaned && cleaned.length >= 5) return cleaned;
  }
  return `STB-${Date.now()}`;
})();

// Extract klantnaam
const customerName = (() => {
  const match = data.trimmedPdfText?.match(/Naam\s+([^\n]+)/);
  if (match) return match[1].trim();

  if (data.filename) {
    const parts = data.filename
      .replace(/\.pdf$/i, '')
      .replace(/^offerte\s*/i, '')
      .trim()
      .split(' ');

    if (parts.length >= 2) {
      return parts.slice(1).join(' ');
    }
  }
  return 'ONBEKEND';
})();

// LIJST VAN BEKENDE STB REGELITEMS
const KNOWN_REGELITEMS = [
  'Hardstenen raamdorpel',
  'Hardstenen onderdorpel',
  'Houtlookverbinding',
  'HVL',
  'Insectenhor',
  'Plisséhordeur',
  'Meerprijs afwijkende kleur',
  'Inclusief vouwhoek',
  'Inclusief koppelstuk',
  'Inclusief extra profielen',
  'Inclusief afwerking',
  'Inclusief roedeverdeling',
  'Inclusief staafgreep',
  'Inclusief HST85',
  'Inclusief thermostep',
  'Inclusief hang- & sluitwerk',
  'Inclusief veiligheidsglas',
  'Breek- en zaagwerk',
  'Inzagen hondenluikje',
  'Afsluitbare kruk',
  'Brievenbus',
  'Inslijpen dakpannen',
  'Zagen dakplaten',
  'Gootstukken',
  'Verduisterend rolgordijn',
  'Binnenafwerking'
];

// Main processing
const elementReviews = [];

(data.merkElements || []).forEach(element => {
  const body = element.body || '';
  const header = element.header || '';

  // Element identificatie
  const elementCode = element.merkCode || 
                     header.replace(/^Merk\s*/i, '').split(/[,.\s]+/)[0];

  const isGekoppeld = element.coupled || body.includes('Gekoppeld');
  const variants = element.variants || [elementCode];

  // Extract basis info
  const elementType = (() => {
    const match = body.match(/Soort kozijn\s+([^\n]+)/);
    if (match) {
      return match[1].replace(/^[A-Z]\d?\.\s*/, '').trim();
    }
    return 'Onbekend type';
  })();

  const dimensions = (() => {
    const match = body.match(/(\d+)x(\d+)mm/);
    return match ? 
      { width: parseInt(match[1]), height: parseInt(match[2]) } : 
      { width: null, height: null };
  })();

  const location = body.match(/Plaats\s+([^\n]+)/)?.[1]?.trim() || '';

  // SIMPELE PARSER - Zoek direct naar bekende items
  const parseElementSimple = (bodyText) => {
    const lines = bodyText.split('\n');

    const result = {
      hoofdproduct: elementType,
      hoofdproductPrijs: 0,
      regelitems: [],
      kortingen: [],
      totaalRegelitems: 0,
      totaalKortingen: 0
    };

    // Vind eerst het hoofdproduct prijs
    // Dit is meestal de eerste prijsregel na "prijs subtotaal"
    let foundPrijsSubtotaal = false;
    let firstPriceFound = false;

    for (let i = 0; i < lines.length; i++) {
      const line = lines[i].trim();

      if (line.toLowerCase() === 'prijs subtotaal') {
        foundPrijsSubtotaal = true;
        continue;
      }

      if (foundPrijsSubtotaal && !firstPriceFound) {
        const priceMatch = line.match(/^(\d+)\s+€\s*([\d.,]+)\s+€\s*([\d.,]+)/);
        if (priceMatch) {
          result.hoofdproductPrijs = parsePrice(priceMatch[3]);
          firstPriceFound = true;
        }
      }

      // Stop bij subtotaal
      if (line.startsWith('Subtotaal €')) {
        break;
      }
    }

    // Nu zoek naar regelitems - gewoon zoeken naar bekende termen
    let currentItem = null;
    let inOmschrijving = false;

    for (let i = 0; i < lines.length; i++) {
      const line = lines[i].trim();

      if (line.toLowerCase().includes('omschrijving') && line.toLowerCase().includes('aantal')) {
        inOmschrijving = true;
        continue;
      }

      if (line.toLowerCase() === 'prijs subtotaal') {
        inOmschrijving = false;
        continue;
      }

      if (line.startsWith('Subtotaal €')) {
        break;
      }

      // Check of deze regel een bekend regelitem bevat
      if (inOmschrijving) {
        for (const knownItem of KNOWN_REGELITEMS) {
          if (line.toLowerCase().includes(knownItem.toLowerCase())) {
            currentItem = line;

            // Zoek de prijs voor dit item
            // Kijk vooruit naar de volgende prijsregels
            for (let j = i + 1; j < Math.min(i + 10, lines.length); j++) {
              const nextLine = lines[j].trim();

              // Check voor prijsregel
              const priceMatch = nextLine.match(/^(\d+)\s+€\s*([\d.,]+)\s+€\s*([\d.,]+)(?:\s+€\s*-([\d.,]+))?$/);
              if (priceMatch) {
                const quantity = parseInt(priceMatch[1]);
                const unitPrice = parsePrice(priceMatch[2]);
                const totalPrice = parsePrice(priceMatch[3]);
                const discount = priceMatch[4] ? parsePrice(priceMatch[4]) : 0;
                const netPrice = discount === totalPrice ? 0 : totalPrice - discount;

                result.regelitems.push({
                  description: discount === totalPrice ? `${currentItem} (-100% korting)` : currentItem,
                  quantity: quantity,
                  unitPrice: unitPrice,
                  price: netPrice
                });

                result.totaalRegelitems += netPrice;
                currentItem = null;
                break;
              }
            }
            break;
          }
        }
      }

      // Check voor kortingsregels
      if (line.match(/^Korting\s*([\d,]+)%?\s+€\s*-([\d.,]+)$/)) {
        const match = line.match(/^Korting\s*([\d,]+)%?\s+€\s*-([\d.,]+)$/);
        const kortingBedrag = parsePrice(match[2]);
        result.kortingen.push({
          description: `Korting ${match[1]}%`,
          quantity: 1,
          unitPrice: -kortingBedrag,
          price: -kortingBedrag
        });
        result.totaalKortingen += kortingBedrag;
      }
    }

    return result;
  };

  // Parse het element
  const pricing = parseElementSimple(body);

  // Extract financiële totalen
  const subtotalMatch = body.match(/Subtotaal\s+€\s*([\d.,]+)/);
  const btwMatch = body.match(/21%\s+btw\s+€\s*([\d.,]+)/);
  const totalMatches = body.match(/Totaal\s+€\s*([\d.,]+)/g);

  const subtotal = subtotalMatch ? parsePrice(subtotalMatch[1]) : null;
  const btw = btwMatch ? parsePrice(btwMatch[1]) : null;

  let totalIncl = null;
  if (totalMatches && totalMatches.length > 0) {
    totalIncl = parsePrice(totalMatches[totalMatches.length - 1].replace(/Totaal\s+€\s*/, ''));
  }

  // Hoofdproduct groep
  const bepaalHoofdproductGroep = (type) => {
    const typeUpper = (type || '').toUpperCase();

    if (typeUpper.includes('VAST RAAM')) return 'STB-KO-V';
    if (typeUpper.includes('DRAAIKIEP')) return 'STB-KO-DK';
    if (typeUpper.includes('DRAAISTOLP')) return 'STB-KO-DK-D';
    if (typeUpper.includes('VOORDEUR')) return 'STB-VD-ST';
    if (typeUpper.includes('ACHTERDEUR')) return 'STB-AD-ST';
    if (typeUpper.includes('SCHUIFPUI')) return 'STB-HSP';
    if (typeUpper.includes('DAKRAAM')) return 'STB-DAKRAAM';
    if (isGekoppeld) return 'STB-KO-VAL';

    return null;
  };

  // Build output
  const elementReview = {
    "Opdrachtnummer": opdrachtnummer,
    "Element ID Ref": variants.join(', '),
    "Element ID": elementCode,
    "Klantnaam": customerName,
    "Element Type": elementType,
    "Locatie": location,
    "Geoffreerde Afmetingen": dimensions.width && dimensions.height ? 
      `${dimensions.width}x${dimensions.height}mm` : null,
    "Breedte (mm)": dimensions.width,
    "Hoogte (mm)": dimensions.height,

    // HOOFDPRODUCT
    "Hoofdproduct": pricing.hoofdproduct,
    "Hoofdproduct Groep": bepaalHoofdproductGroep(elementType),
    "Hoofdproduct Prijs": pricing.hoofdproductPrijs || 0,

    // REGELITEMS
    "Totaal Regelitems": pricing.regelitems.length,
    "Totaal Regelitems Prijs": pricing.totaalRegelitems,
    "Alle Regelitems Detail": pricing.regelitems.length > 0 ? 
      pricing.regelitems.map(r => 
        `${r.description}: ${r.quantity}x €${r.unitPrice || 0} = €${r.price || 0}`
      ).join('\n') : null,
  };

  // Voeg individuele regelitems toe
  for (let i = 0; i < 10; i++) {
    const item = pricing.regelitems[i];
    if (item) {
      elementReview[`Regelitem ${i + 1}`] = item.quantity > 1 ? 
        `${item.description} (${item.quantity}x)` : item.description;
      elementReview[`Regelitem ${i + 1} Prijs`] = item.price || 0;
    } else {
      elementReview[`Regelitem ${i + 1}`] = null;
      elementReview[`Regelitem ${i + 1} Prijs`] = null;
    }
  }

  // KORTINGEN
  elementReview["Element Korting"] = pricing.totaalKortingen;
  elementReview["Kortingen Detail"] = pricing.kortingen.length > 0 ?
    pricing.kortingen.map(k => `${k.description}: €${k.price}`).join('\n') : null;

  // TOTALEN
  elementReview["Element Subtotaal"] = subtotal;
  elementReview["Element Totaal"] = subtotal ? (subtotal - pricing.totaalKortingen) : subtotal;
  elementReview["Element BTW"] = btw;
  elementReview["Element Totaal Incl BTW"] = totalIncl;
  elementReview["Definitieve Element Prijs"] = totalIncl;

  // STATUS
  elementReview["Verkoop Review Status"] = "Concept";
  elementReview["Auto Cost Lookup Status"] = "Niet geprobeerd";
  elementReview["Review Notities"] = isGekoppeld ? "Gekoppeld element" : null;

  // Clean nulls
  const cleaned = {};
  Object.keys(elementReview).forEach(key => {
    const value = elementReview[key];
    if (value !== null && value !== undefined && value !== '') {
      cleaned[key] = typeof value === 'number' && 
        (key.includes('Prijs') || key.includes('Totaal') || 
         key.includes('BTW') || key.includes('Korting')) ? 
        Math.round(value * 100) / 100 : value;
    }
  });

  elementReviews.push(cleaned);
});

return elementReviews.map(element => ({ json: element }));

// Pak de data uit de eerste node
const items = $input.all();
const data = items[0].json;

// Parse helper
const parsePrice = (str) => {
 if (!str) return null;
 return parseFloat(str.replace(/\./g, '').replace(',', '.'));
};

// Extract basis info
const opdrachtnummer = data.opdrachtnummer || 
                     data.filename?.replace(/^Offerte\s*/, '').replace(/\.pdf$/, '').trim() ||
                     null;

// Extract klantgegevens DYNAMISCH
const klantNaam = data.trimmedPdfText?.match(/Naam\s+(.+)/)?.[1]?.trim() || null;
const klantAdres = data.trimmedPdfText?.match(/Adres\s+(.+)/)?.[1]?.trim() || null;
const klantPostcode = data.trimmedPdfText?.match(/Postcode\s+(.+)/)?.[1]?.trim() || null;
const klantStad = data.trimmedPdfText?.match(/Plaats\s+(.+)/)?.[1]?.trim() || null;
const klantTelefoon = data.trimmedPdfText?.match(/Telefoon\s+(.+)/)?.[1]?.trim() || null;
const klantEmail = data.trimmedPdfText?.match(/E-mail\s+(.+)/)?.[1]?.trim() || null;

// Tel totaal aantal elementen
const totalElements = data.merkElements ? data.merkElements.length : 0;

// Bereken geschatte uren (18 minuten per element)
const estimatedMinutes = totalElements * 18;
const estimatedHours = Math.round((estimatedMinutes / 60) * 10) / 10;

// Extract unieke locaties uit merkElements
const uniqueLocations = [...new Set(
 (data.merkElements || []).map(el => {
   const location = el.body?.match(/Plaats\s+(.+?)(?:\n|$)/)?.[1]?.trim();
   return location || 'Onbekend';
 })
)].filter(loc => loc !== 'Onbekend');

// Extract totaalbedrag uit summaryText
const totaalBedrag = data.summaryText?.match(/Totaal\s+€\s*([\d.,]+)/)?.[1];
const grandTotal = totaalBedrag ? parsePrice(totaalBedrag) : null;

// Maak elementen overzicht
const elementenOverzicht = (data.merkElements || []).map(el => {
 const elementType = el.body?.match(/Soort kozijn\s+(.+?)(?:\n|$)/)?.[1]?.trim() || 
                    el.body?.match(/Soort decoratie\s+(.+?)(?:\n|$)/)?.[1]?.trim() || 
                    'Onbekend';
 return `${el.header} (${elementType})`;
}).join(' | ');

// Bepaal provincie op basis van postcode (eerste 2 cijfers)
const provincie = klantPostcode ? klantPostcode.substring(0, 2) + 'xx' : null;

// Verkoopdatum (vandaag of uit data)
const verkoopdatum = data.trimmedPdfText?.match(/Offerte datum\s+(.+)/)?.[1]?.trim() || 
                   new Date().toISOString().split('T')[0];

// Create Inmeetplanning record
const inmeetData = {
 // Primary identification
 "Opdrachtnummer": opdrachtnummer,

 // Main overview fields
 "Klant & Stad": klantNaam && klantStad ? `${klantNaam} - ${klantStad}` : null,
 "Telefoon": klantTelefoon,
 "Elementen": totalElements,
 "Uren": estimatedHours,
 "Waarde": grandTotal,
 "Projectstatus": "Planning Vereist",

 // Detailed customer information
 "Klantnaam": klantNaam,
 "E-mail": klantEmail,
 "Volledig Adres": [klantAdres, `${klantPostcode} ${klantStad}`].filter(Boolean).join('\n') || null,
 "Stad": klantStad,
 "Postcode": klantPostcode,
 "Provincie": provincie,

 // Project details
 "Opdracht verkocht op": verkoopdatum,
 "Total Amount Incl BTW": grandTotal,
 "Aantal Elementen": totalElements,
 "Elementen Overzicht": elementenOverzicht || null,
 "Locaties": uniqueLocations.join(' | ') || null,
 "Geschatte Uren": estimatedHours,
 "Planning Notities": `Totaal elementen: ${totalElements} | Locaties: ${uniqueLocations.length} verschillende gebieden | Geschat: ${estimatedHours} uur (${estimatedMinutes} min)`,
 "Uiterlijke montagedatum": null
};

// Clean null values voor Airtable
const cleanedData = {};
Object.keys(inmeetData).forEach(key => {
 if (inmeetData[key] !== null && inmeetData[key] !== undefined) {
   cleanedData[key] = inmeetData[key];
 }
});

return [{ json: cleanedData }];

// Pak de data uit de eerste node
const items = $input.all();
const data = items[0].json;

// Parse helper
const parsePrice = (str) => {
  if (!str) return null;
  return parseFloat(str.replace(/\./g, '').replace(',', '.'));
};

// Extract basis info
const opdrachtnummer = data.opdrachtnummer || 
                      data.filename?.replace(/^Offerte\s*/, '').replace(/\.pdf$/, '').trim() ||
                      null;

// Extract klantgegevens DYNAMISCH
const klantNaam = data.trimmedPdfText?.match(/Naam\s+(.+)/)?.[1]?.trim() || null;
const klantAdres = data.trimmedPdfText?.match(/Adres\s+(.+)/)?.[1]?.trim() || null;
const klantPostcode = data.trimmedPdfText?.match(/Postcode\s+(.+)/)?.[1]?.trim() || null;
const klantStad = data.trimmedPdfText?.match(/Plaats\s+(.+)/)?.[1]?.trim() || null;
const klantTelefoon = data.trimmedPdfText?.match(/Telefoon\s+(.+)/)?.[1]?.trim() || null;
const klantEmail = data.trimmedPdfText?.match(/E-mail\s+(.+)/)?.[1]?.trim() || null;

// Extract totaalbedrag uit summaryText
const totaalMatch = data.summaryText?.match(/Totaal\s+€\s*([\d.,]+)(?!.*Totaal)/s);
const totalAmount = totaalMatch ? parsePrice(totaalMatch[1]) : 0;

// Bereken factuur bedragen (30%, 65%, 5%)
const voorafAmount = Math.round((totalAmount * 0.30) * 100) / 100;
const startAmount = Math.round((totalAmount * 0.65) * 100) / 100;
const opleveringAmount = Math.round((totalAmount * 0.05) * 100) / 100;

// Maak volledig adres
const volledigAdres = [
  klantAdres,
  `${klantPostcode} ${klantStad}`
].filter(Boolean).join('\n') || null;

// Maak factuur records - LET OP: geen dubbele spaties in Type Factuur!
const facturen = [
  {
    "Factuur ID": `${opdrachtnummer}-001`,
    "Opdrachtnummer": opdrachtnummer,
    "Type Factuur": "30% - Vooraf",
    "Bedrag": voorafAmount,
    "Status": "Concept",
    "Verstuurd op": null,
    "Vervaldatum": null,
    "Klant": klantNaam,
    "Email": klantEmail,
    "Telefoon": klantTelefoon,
    "Adres": volledigAdres
  },
  {
    "Factuur ID": `${opdrachtnummer}-002`,
    "Opdrachtnummer": opdrachtnummer,
    "Type Factuur": "65% - Start",  // Geen dubbele spatie meer!
    "Bedrag": startAmount,
    "Status": "Concept",
    "Verstuurd op": null,
    "Vervaldatum": null,
    "Klant": klantNaam,
    "Email": klantEmail,
    "Telefoon": klantTelefoon,
    "Adres": volledigAdres
  },
  {
    "Factuur ID": `${opdrachtnummer}-003`,
    "Opdrachtnummer": opdrachtnummer,
    "Type Factuur": "5% - Oplevering",
    "Bedrag": opleveringAmount,
    "Status": "Concept",
    "Verstuurd op": null,
    "Vervaldatum": null,
    "Klant": klantNaam,
    "Email": klantEmail,
    "Telefoon": klantTelefoon,
    "Adres": volledigAdres
  }
];

// Return elk factuur als apart item voor Airtable batch processing
return facturen.map(factuur => {
  // Clean null values voor Airtable
  const cleanedFactuur = {};
  Object.keys(factuur).forEach(key => {
    if (factuur[key] !== null && factuur[key] !== undefined) {
      cleanedFactuur[key] = factuur[key];
    }
  });
  return { json: cleanedFactuur };
});

// n8n Code Node — Eén node, alles-in-één
// Verwacht: items[0].json.fullPdfText (fallbacks: fullPDFtext, pdfText)

const raw =
  (items[0].json.fullPdfText || items[0].json.fullPDFtext || items[0].json.pdfText || "")
  .toString();

// 0) Hard exit als leeg
if (!raw.trim()) {
  return [{ json: { error: "Lege of ontbrekende fullPdfText", trimmedPdfText: "", summaryText: "", merkElements: [] }}];
}

// 1) Normalisatie & snelle OCR-ruis opschoning
let text = raw
  .replace(/\u0000/g, "")                  // null-bytes
  .replace(/\r/g, "\n")
  .replace(/[ \t]+/g, " ")
  .replace(/\n{3,}/g, "\n\n")              // overbodige lege regels
  .replace(/\s+€/g, " €")                  // spatie-normalisatie voor euro
  // euro-waarden waarin OCR letters aan eind plakt, bv "€ 1.289,38ze" -> "€ 1.289,38"
  .replace(/€\s*([\d\.\,]+)[a-zA-Z]+/g, "€ $1")
  // rare OCR-samenvoeging van “officiële” etc maakt ons niet uit
  ;

// 2) Werkwijze/FAQ-blok verwijderen, maar "Samenvatting prijs" behouden
//   We knippen van "Werkwijze STB[- ]?kozijnen" t/m vlak vóór "Samenvatt(ing|ende) prijs"
const procBlock = /Werkwijze\s*STB[-\s]?kozijnen[\s\S]*?(?=Samenvatt(?:ing|ende)\s+prijs)/i;
const hadProcedureBlock = procBlock.test(text);
if (hadProcedureBlock) {
  text = text.replace(procBlock, ""); // laat "Samenvatting prijs" staan
}

// Als het werkwijze-anker niet gevonden is, probeer ook een fallback op “Frequently Asked Questions”
const faqBlock = /Frequently\s+Asked\s+Questions[\s\S]*?(?=Samenvatt(?:ing|ende)\s+prijs)/i;
const hadFaqBlock = faqBlock.test(text);
if (!hadProcedureBlock && hadFaqBlock) {
  text = text.replace(faqBlock, "");
}

// 3) Bepaal summary start (Samenvatting prijs) en knip summaryText uit
let summaryStartIdx = -1;
let summaryText = "";
{
  const sm = text.match(/Samenvatt(?:ing|ende)\s+prijs/i);
  if (sm) {
    summaryStartIdx = sm.index;
    summaryText = text.slice(summaryStartIdx).trim();
  }
}

// 4) Trimmed tekst: hele document zonder werkwijze/FAQ, met eventuele summary intact
const trimmedPdfText = text.trim();

// 5) Merk-blokken detecteren tot aan summary (zodat de samenvatting geen merk-blokken breekt)
const searchCeiling = summaryStartIdx > -1 ? summaryStartIdx : trimmedPdfText.length;

// Merk-header: begin van regel, "Merk" + rest van de regel
const headerRegex = /(^|\n)\s*(Merk[^\n]*)/gi;
let headers = [];
let m;
while ((m = headerRegex.exec(trimmedPdfText)) !== null) {
  const idx = m.index + (m[1] ? m[1].length : 0);
  if (idx < searchCeiling) {
    headers.push({ index: idx, text: m[2].trim() });
  }
}

// Geen merk-headers? Dan geven we alsnog trimmed + summary terug
if (!headers.length) {
  return [{
    json: {
      trimmedPdfText,
      summaryText,
      merkElements: [],
      _debug: {
        hadProcedureBlock,
        hadFaqBlock,
        summaryFound: summaryStartIdx > -1
      }
    }
  }];
}

// 6) Snij blokken tussen headers
const blocks = [];
for (let i = 0; i < headers.length; i++) {
  const start = headers[i].index;
  const end = (i < headers.length - 1)
    ? Math.min(headers[i + 1].index, searchCeiling)
    : searchCeiling;
  blocks.push({
    header: headers[i].text,
    start,
    end,
    body: trimmedPdfText.slice(start, end).trim()
  });
}

// Helpers voor header-analyse
function extractDVariants(h) {
  // pakt D1, D2, D3, etc. (maakt niet uit of er punten of streepjes tussen staan)
  const variants = h.match(/\b([A-Z]\d+)\b/g);
  if (!variants || variants.length < 2) return null;
  const firstLetter = variants[0][0];
  const sameLetter = variants.every(v => v[0] === firstLetter);
  if (!sameLetter) return null;
  return { merkCode: firstLetter, variants: Array.from(new Set(variants)), coupled: true };
}

function extractMultiLetters(h) {
  // “Merk B, C” -> ["B","C"]
  const m = h.match(/\bMerk\s+([A-Z](?:\s*,\s*[A-Z])+)\b/i);
  if (!m) return null;
  return m[1].split(/\s*,\s*/).filter(Boolean);
}

function extractSingleLetter(h) {
  const m = h.match(/\bMerk\s+([A-Z])\b/);
  return m ? m[1] : null;
}

// 7) Bouw merkElements met regels:
//    - “Merk D1. D2.” -> één element (coupled: true, variants: ["D1","D2"])
//    - “Merk B, C” -> twee losse elementen (elk coupled: false)
//    - “Merk B” -> één element
const merkElements = [];

for (const b of blocks) {
  const cleanHeader = b.header.replace(/\s+/g, " ").trim();

  // Case 1: D-varianten (één gekoppeld element)
  const dRes = extractDVariants(cleanHeader);
  if (dRes) {
    merkElements.push({
      header: `Merk ${dRes.variants.join(". ")}`,
      originalHeader: b.header,
      merkCode: dRes.merkCode,
      variants: dRes.variants,
      coupled: true,
      multiHeaderLettersSplit: false,
      body: b.body
    });
    continue;
  }

  // Case 2: Meerdere letters in één header (B, C → 2 elementen)
  const letters = extractMultiLetters(cleanHeader);
  if (letters && letters.length > 1) {
    letters.forEach(L => {
      merkElements.push({
        header: `Merk ${L}`,
        originalHeader: b.header,
        merkCode: L,
        variants: [L],
        coupled: false,
        multiHeaderLettersSplit: true,
        body: b.body
      });
    });
    continue;
  }

  // Case 3: Enkelvoudige letter
  const single = extractSingleLetter(cleanHeader);
  if (single) {
    merkElements.push({
      header: `Merk ${single}`,
      originalHeader: b.header,
      merkCode: single,
      variants: [single],
      coupled: false,
      multiHeaderLettersSplit: false,
      body: b.body
    });
    continue;
  }

  // Fallback: onbekend patroon, geef door als-is
  merkElements.push({
    header: b.header,
    originalHeader: b.header,
    merkCode: null,
    variants: [],
    coupled: false,
    multiHeaderLettersSplit: false,
    body: b.body
  });
}

// 8) Extra kleine opschoning op elke body (optioneel; helpt GPT):
const normalizeBody = (s) => s
  .replace(/Pagina \d+ van \d+/gi, "")
  .replace(/Omschrijving\s+aantal/gi, "Omschrijving Aantal")
  .replace(/\n{3,}/g, "\n\n")
  .trim();

merkElements.forEach(e => { e.body = normalizeBody(e.body); });

// 9) Resultaat
return [{
  json: {
    trimmedPdfText,
    summaryText,
    merkElements,
    _debug: {
      hadProcedureBlock,
      hadFaqBlock,
      summaryFound: summaryStartIdx > -1,
      headersFound: headers.length,
    }
  }
}];

